		$this->assertEquals( "http://www.islands.co.il/he/", transposh_utils::rewrite_url_lang_param( "http://www.islands.co.il/he/", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://www.islands.co.il/fr/", transposh_utils::rewrite_url_lang_param( "http://www.islands.co.il/fr/", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/he", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/fr", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/he/", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/fr/", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/zh-tw/", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/37/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/37/", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/fr/?lang=zh", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/fr/?lang=zh&edit=1", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/fr/?lang=zh-tw&edit=1", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/fr/?lang=zh&amp;edit=1", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/fr/?lang=zh&#038;edit=1", $homeurl, $permalinks, "he", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?cat=y&lang=he", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/fr/?lang=zh&#038;edit=1&cat=y", $homeurl, $permalinks, "he", $edit, $params_only ) );

		$this->assertEquals( "", transposh_utils::rewrite_url_lang_param( "", $homeurl, $permalinks, "", $edit, $params_only ) );
		$this->assertEquals( "/", transposh_utils::rewrite_url_lang_param( "/", $homeurl, $permalinks, "", $edit, $params_only ) );
		$this->assertEquals( "http://transposh.org/test/?cat=y", transposh_utils::rewrite_url_lang_param( "http://transposh.org/test/fr/?lang=zh&#038;edit=1&cat=y", $homeurl, $permalinks, "", $edit, $params_only ) );
	}

	public function testCleanupURL() {
		$homeurl = "http://www.algarve-abc.de/ferienhaus-westalgarve/";
		$this->assertEquals( "http://www.algarve-abc.de/ferienhaus-westalgarve/test", transposh_utils::cleanup_url( "http://www.algarve-abc.de/ferienhaus-westalgarve/en/test", $homeurl ) );
		$this->assertEquals( "http://www.algarve-abc.de/ferienhaus-westalgarve", transposh_utils::cleanup_url( "http://www.algarve-abc.de/ferienhaus-westalgarve", $homeurl ) );
		$this->assertEquals( "http://www.algarve-abc.de/ferienhaus-westalgarve", transposh_utils::cleanup_url( "http://www.algarve-abc.de/ferienhaus-westalgarve/en", $homeurl ) );
		$this->assertEquals( "http://www.algarve-abc.de/ferienhaus-westalgarve/", transposh_utils::cleanup_url( "http://www.algarve-abc.de/ferienhaus-westalgarve/en/", $homeurl ) );
		$this->assertEquals( "/ferienhaus-westalgarve/", transposh_utils::cleanup_url( "http://www.algarve-abc.de/ferienhaus-westalgarve/en/", $homeurl, true ) );
	}

