	public function testGetOriginalURL() {
		$callback = static function ( $str = "", $param = "" ) {
			return $str;
		};
		$this->assertEquals( "/", transposh_utils::get_original_url( "/", '', 'en', $callback ) );
		$this->assertEquals( "/test", transposh_utils::get_original_url( "/test", '', 'en', $callback ) );
		$this->assertEquals( "/test/", transposh_utils::get_original_url( "/test/", '', 'en', $callback ) );
		$this->assertEquals( "www.islands.co.il/he/test/", transposh_utils::get_original_url( "www.islands.co.il/he/test/", 'www.islands.co.il', 'en', $callback ) );
		$this->assertEquals( "http://www.islands.co.il/he/test/", transposh_utils::get_original_url( "http://www.islands.co.il/he/test/", 'http://www.islands.co.il', 'en', $callback ) );
	}

	public function testTranslateURL() {
		$callback = static function ( $str = "", $param = "" ) {
			return array( $str, '0' );
		};
		$this->assertEquals( "/", transposh_utils::translate_url( "/", '', 'en', $callback ) );
		$this->assertEquals( "/test", transposh_utils::translate_url( "/test", '', 'en', $callback ) );
		$this->assertEquals( "/test/", transposh_utils::translate_url( "/test/", '', 'en', $callback ) );
		$this->assertEquals( "www.islands.co.il/he/test/", transposh_utils::translate_url( "www.islands.co.il/he/test/", 'www.islands.co.il', 'en', $callback ) );
		$this->assertEquals( "www.islands.co.il?lang=he", transposh_utils::translate_url( "www.islands.co.il?lang=he", 'www.islands.co.il', 'en', $callback ) );
		$this->assertEquals( "http://www.islands.co.il/he/test/", transposh_utils::translate_url( "http://www.islands.co.il/he/test/", 'http://www.islands.co.il', 'en', $callback ) );
		$this->assertEquals( "http://www.islands.co.il/he/test/?edit=1", transposh_utils::translate_url( "http://www.islands.co.il/he/test/?edit=1", 'http://www.islands.co.il', 'en', $callback ) );
	}

	public function testGrabLanguage() {
		$home_url = "http://transposh.org";
		$this->assertEquals( "he", transposh_utils::get_language_from_url( "http://transposh.org/he/test/", $home_url ) );
		$this->assertEquals( "he", transposh_utils::get_language_from_url( "http://transposh.org/he", $home_url ) );
		$this->assertEquals( "he", transposh_utils::get_language_from_url( "http://transposh.org/?lang=he", $home_url ) );
		$this->assertEquals( "he", transposh_utils::get_language_from_url( "http://transposh.org/he/?lang=he", $home_url ) );
		$this->assertEquals( "he", transposh_utils::get_language_from_url( "http://transposh.org/he/?lang=he&fakeparam=no", $home_url ) );
		$this->assertEquals( "he", transposh_utils::get_language_from_url( "http://transposh.org/he/?fake=no&lang=he&fakeparam=no", $home_url ) );
		$this->assertEquals( "he", transposh_utils::get_language_from_url( "http://transposh.org/hello/test/?lang=he", $home_url ) );
	}

